	Set:$ClassName(args(2))'="otzi.core.vo.Template" tSC = $$$ERROR(6405,"Template","otzi.core.vo.Template")
	Throw:$$$ISERR(tSC)
} Catch tException {
	Set:$$$ISOK(tSC) tSC = tException.AsStatus()
