Set routes = "<Routes>" _ routes _ "</Routes>" Set route = ##class(%Dictionary.XDataDefinition).%New() Set route.Name = "UrlMap" Do route.Data.Write(routes) $$$THROWONERROR(tSC, newClass.XDatas.Insert(route)) } $$$THROWONERROR(tSC, newClass.%Save() )
