ZNSpace ns Do xdata.Data.Write(..Replace(pPackage, pProject, pTemplate.Bpl.Read())) ZNSpace pNamespace
$$$THROWONERROR(tSC, newClass.XDatas.Insert(xdata)) } $$$THROWONERROR(tSC, newClass.%Save() )
If ((pTemplate.Page.Style.Read()'="")||(pTemplate.Page.Content.Read()'="")) {
Set content = ##class(%GlobalCharacterStream).%New(), page = $ListBuild("Style","Contents")
For p=1:1:$ListLength(page) { Do:p=1 content.CopyFrom(pTemplate.Page.Style) Do:p=2 content.CopyFrom(pTemplate.Page.Content) Do content.Rewind() If content.Read()'="" { Do content.Rewind() Set xdata = ##class(%Dictionary.XDataDefinition).%New() Set xdata.Name = $List(page, p) ;Set xdata.Description = ""
ZNSpace ns Do xdata.Data.Write(..Replace(pPackage, pProject, content.Read())) ZNSpace pNamespace
$$$THROWONERROR(tSC, newClass.XDatas.Insert(xdata)) } } $$$THROWONERROR(tSC, newClass.%Save() ) }
