Set tSC = $$$OK, pModule = ##class(otzi.core.vo.Engine.Module).%New()
Set:pVersion="" pVersion = "^0"
Try {
	Set max = ..Engine.Modules.Count(), curr = 0

	While (curr < max){
		Set curr = $Increment(curr)
		#Dim module As otzi.core.vo.Engine.Module
		Set module = ..Engine.Modules.GetAt(curr)
		If ((module.Name = pModuleName)&&(..IsThisVersion(pVersion, module.Version))) {
			Set curr = max + 1, pModule = module
		}
	}
} Catch tException {
	Set:$$$ISOK(tSC) tSC = tException.AsStatus()
}
Quit tSC

Set tSC = $$$OK, output = $$$NO Try { Set matcher = ##class(%Regex.Matcher).%New("[0-9\.]") Set matcher.Text = pVersion, condition = matcher.ReplaceAll(""), version = $Replace(pVersion,condition,""), matcher.Text = pModuleVersion, moduleVersion = $Replace(pModuleVersion,matcher.ReplaceAll(""),"")
Set condition = $Replace(condition," ","") Set:((condition="")&&(version = moduleVersion)) output = $$$YES Set:(((condition=">=")||(condition="~")||(condition="^")||(condition="u")|| (condition="up")||(condition="&gt;="))&&(version <= moduleVersion)) output = $$$YES Set:(((condition="<=")||(condition="&lt;=")||(condition="d")|| (condition="down"))&&(version >= moduleVersion)) output = $$$YES Set:(((condition=">")||(condition="&gt;")|| (condition="+"))&&(version < moduleVersion)) output = $$$YES Set:(((condition="<")||(condition="&lt;")|| (condition="-"))&&(version > moduleVersion)) output = $$$YES
} Catch tException { Set:$$$ISOK(tSC) tSC = tException.AsStatus() } Quit output

