Set tSC = $$$OK ZNSpace "%SYS" Try { Set LowName = $ZConvert(Name,"L")
//Create directories Set Path = ##class(%File).ManagerDirectory() _ Name Set tSC = ##class(%File).CreateDirectory(Path) Return:$$$ISERR(tSC) tSC Set PathWEB = ##class(%File).NormalizeDirectory(##class(%File).ParentDirectoryName(##class(%File).ManagerDirectory()) _ "CSP") _ Name Set tSC = ##class(%File).CreateDirectory(PathWEB) Return:$$$ISERR(tSC) tSC
// Create DataBase File Set tSC=##Class(SYS.Database).CreateDatabase(Path) Return:$$$ISERR(tSC) tSC
// Mount database to Cache Set Properties("Directory")=Path Set Properties("MountRequired")=1 Set tSC=##Class(Config.Databases).Create(Name,.Properties) Return:$$$ISERR(tSC) tSC Set Properties("MountRequired")=1 Set tSC=##Class(Config.Databases).Modify(Name,.Properties)
