// Create web apps
// default if ((WebApps = 1) || (WebApps = -1)) { Set cspProperties("AutheEnabled") = 64 Set cspProperties("NameSpace") = Name Set cspProperties("Path") = PathWEB Set cspProperties("IsNameSpaceDefault") = 1 Set tSC = ##Class(Security.Applications).Create("/csp/"_LowName,.cspProperties) Return:$$$ISERR(tSC) tSC }
// broker if ((WebApps = 2) || (WebApps = -1)) { Set cspProperties("AutheEnabled") = 64 Set cspProperties("NameSpace") = Name Set cspProperties("DispatchClass") = "REST.Broker" Set cspProperties("IsNameSpaceDefault") = 0 Set tSC = ##Class(Security.Applications).Create("/broker/"_LowName,.cspProperties) Return:$$$ISERR(tSC) tSC } } Catch tException { Set:$$$ISOK(tSC) tSC = tException.AsStatus() }
Quit tSC

Set pEnv=$TRANSLATE($ZCONVERT(pEnv,"U")," ","")
Set tSC = $$$OK, Env = $$$NO
Try {
	Set:pEnv="" Env = $$$YES
	Set:((pEnv="ENS")&&($Find(..Ensemble(pNameSpace),"Ensemble"))) Env = $$$YES
} Catch tException {
	Set:$$$ISOK(tSC) tSC = tException.AsStatus()
}
Quit Env

Set tSC = $$$OK, sys = "", ns=$Namespace
Try {
	Set statement = ##class(%SQL.Statement).%New()
	Set tSC = statement.%Prepare("SELECT ID FROM %Dictionary.ClassDefinition Where Name = 'Ens.Director'")

	ZNSpace pNameSpace
		Set tRS = statement.%Execute()

		While tRS.%Next() {
			Set:tRS.%Get("ID")'="" sys = "Ensemble" _ $System.Version.Format(5)
		}
	ZNSpace ns

} Catch tException {
	Set:$$$ISOK(tSC) tSC = tException.AsStatus()
}
Quit sys

Set modules = $ListFromString(pName,",")
For k=1:1:$ListLength(modules){
	Do ..Modules.Insert($List(modules,k))
}

Quit $This

Set tSC = $$$OK
Try {
	$$$THROWONERROR(tSC, ##class(otzi.core.Project).Create(..NameSpace, ..Package, ..Project, ..Modules, ..Test, ..WebApp, ..Options ))
} Catch tException {
	Set:$$$ISOK(tSC) tSC = tException.AsStatus()
}
Quit tSC
